// src/pages/FollowersPage.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate }    from 'react-router-dom';
import { supabase }                  from '../lib/supabaseClient';
import { avatarList }                from '../utils/avatars';
import './FollowersPage.css';

export default function FollowersPage() {
  const { userId } = useParams();        // trader_id
  const navigate  = useNavigate();
  const [loading, setLoading]     = useState(true);
  const [followers, setFollowers] = useState([]);

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      // get list of investor_ids
      const { data: rows, error: fErr } = await supabase
        .from('follows')
        .select('investor_id')
        .eq('trader_id', userId);
      if (fErr) {
        console.error(fErr);
        return setLoading(false);
      }
      const ids = rows.map(r => r.investor_id);
      if (!ids.length) {
        setFollowers([]);
        return setLoading(false);
      }
      // fetch their profiles
      const { data: profs, error: pErr } = await supabase
        .from('profiles')
        .select('user_id, full_name, avatar_url')
        .in('user_id', ids);
      if (pErr) {
        console.error(pErr);
        setLoading(false);
      } else {
        setFollowers(profs);
        setLoading(false);
      }
    };
    load();
  }, [userId]);

  if (loading) {
    return <div className="followers-page loader">Y√ºkleniyor‚Ä¶</div>;
  }

  return (
    <div className="followers-page">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Geri</button>
      <h2>Takip√ßiler</h2>
      {followers.length === 0 ? (
        <p>Hen√ºz takip√ßi yok.</p>
      ) : (
        <ul className="followers-list">
          {followers.map(f => {
            const imgSrc = avatarList.find(a => a.id === f.avatar_url)?.image;
            return (
              <li key={f.user_id} className="follower-item">
                {imgSrc
                  ? <img src={imgSrc} alt="" className="avatar" />
                  : <div className="avatar-placeholder">üë§</div>
                }
                <span className="name">{f.full_name || 'Anonim'}</span>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
