import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { supabase } from './lib/supabaseClient';

import AuthPage from './pages/AuthPage';
import ProfilePage from './pages/ProfilePage';
// ... ihtiyacınıza göre HomePage, MarketPage, NewsPage

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Başlangıçta mevcut oturumu al
  useEffect(() => {
    const initAuth = async () => {
      const { data } = await supabase.auth.getSession();
      setUser(data.session?.user ?? null);
      setLoading(false);
    };
    initAuth();

    // Oturum değişikliklerini dinle
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      (_event, session) => {
        setUser(session?.user ?? null);
      }
    );

    return () => {
      subscription.unsubscribe();
    };
  }, []);

  if (loading) {
    // Oturum kontrolü yapılana kadar basit bir yükleme ekranı
    return <div style={{ textAlign: 'center', marginTop: 100 }}>Yükleniyor…</div>;
  }

  return (
    <Routes>
      <Route path="/auth" element={<AuthPage />} />

      {/* Korunmuş rota örneği */}
      <Route
        path="/profile"
        element={user ? <ProfilePage /> : <Navigate to="/auth" replace />}
      />

      {/* Fallback: kullanıcı varsa profile, yoksa auth */}
      <Route
        path="*"
        element={<Navigate to={user ? "/profile" : "/auth"} replace />}
      />
    </Routes>
  );
}

export default App;
