// src/pages/HomePage.js
import React, { useContext, useEffect, useState } from 'react';
import { useNavigate }            from 'react-router-dom';
import { SignalsContext }         from '../context/SignalsContext';
import { supabase }               from '../lib/supabaseClient';
import './HomePage.css';
import { FaThumbsUp, FaThumbsDown, FaComment } from 'react-icons/fa';

export default function HomePage() {
  const navigate   = useNavigate();
  const { signals }= useContext(SignalsContext);
  const [user, setUser]               = useState(null);
  const [profilesMap, setProfilesMap] = useState({});
  const [reactionsMap, setReactionsMap]= useState({});
  const [commentsCountMap, setCommentsCountMap]= useState({});
  const [loading, setLoading]         = useState(true);

  useEffect(() => {
    supabase.auth.getSession().then(({ data:{ session } }) => {
      if (session?.user) setUser(session.user);
    });
  }, []);

  useEffect(() => {
    if (!user || !signals.length) return;
    async function fetchAll() {
      setLoading(true);
      // profiller
      let { data: profs } = await supabase
        .from('profiles')
        .select('user_id, full_name');
      const pMap = {};
      profs.forEach(p => pMap[p.user_id] = p.full_name || 'Anonim');
      setProfilesMap(pMap);

      // reaksiyonlar
      let { data: reacts } = await supabase
        .from('reactions')
        .select('signal_id, user_id, type');
      const rMap = {};
      signals.forEach(s => rMap[s.id] = { likeCount:0, dislikeCount:0, myType:null });
      reacts.forEach(r => {
        const e = rMap[r.signal_id];
        if (!e) return;
        if (r.type === 'like')  e.likeCount++;
        else                    e.dislikeCount++;
        if (r.user_id === user.id) e.myType = r.type;
      });
      setReactionsMap(rMap);

      // yorum sayıları
      let { data: comms } = await supabase
        .from('comments')
        .select('signal_id');
      const cMap = {};
      signals.forEach(s => cMap[s.id] = 0);
      comms.forEach(c => { if (cMap[c.signal_id] !== undefined) cMap[c.signal_id]++; });
      setCommentsCountMap(cMap);

      setLoading(false);
    }
    fetchAll();
  }, [user, signals]);

  const handleReaction = async (signalId, type) => {
    const prev = reactionsMap[signalId]?.myType;
    if (prev === type) {
      await supabase
        .from('reactions')
        .delete()
        .match({ signal_id: signalId, user_id: user.id });
    } else if (prev) {
      await supabase
        .from('reactions')
        .update({ type })
        .match({ signal_id: signalId, user_id: user.id });
    } else {
      await supabase
        .from('reactions')
        .insert({ signal_id: signalId, user_id: user.id, type });
    }
    // yenile
    const { data: reacts } = await supabase
      .from('reactions')
      .select('signal_id, user_id, type');
    const rMap = {};
    signals.forEach(s => rMap[s.id] = { likeCount:0, dislikeCount:0, myType:null });
    reacts.forEach(r => {
      const e = rMap[r.signal_id];
      if (!e) return;
      if (r.type === 'like')  e.likeCount++;
      else                    e.dislikeCount++;
      if (r.user_id === user.id) e.myType = r.type;
    });
    setReactionsMap(rMap);
  };

  const goComments = id => {
    navigate(`/comments/${id}`);
  };

  if (loading) return <div className="loader">Yükleniyor…</div>;

  return (
    <div className="home-page">
      {/* -- üst başlık ve genel arama çubuğu zaten App.js içinde */}
      <ul className="signals-list">
        {signals.map(item => {
          const date = new Date(item.timestamp).toLocaleDateString();
          const time = new Date(item.timestamp).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});
          const username = profilesMap[item.userId] || 'Anonim';
          const r = reactionsMap[item.id] || {};
          const cCount = commentsCountMap[item.id] || 0;

          return (
            <li key={item.id} className="signal-card">
              <div className="user-row">
                <FaComment className="user-icon" /> {/* avatar yerine ikon */}
                <span className="user-name">{username}</span>
              </div>
              <div className="header-row">
                <h3>{item.symbol}</h3>
                <small>{date} {time}</small>
              </div>
              <div className="meta-row">
                <span className={`badge ${item.direction==='LONG'?'long':'short'}`}>{item.direction}</span>
                <span className="badge timeframe">{item.timeFrame.toUpperCase()}</span>
              </div>
              <div className="divider" />
              {/* price rows */}
              <div className="row"><span>Entry Price</span><span>{item.entryPrice}</span></div>
              <div className="row"><span>Leverage</span><span>{item.recommendedLeverage}x</span></div>
              <div className="divider" />
              {item.targets.map((t,i)=>
                <div key={i} className="row">
                  <span>Target {i+1}</span>
                  <span className="target">{t}</span>
                </div>
              )}
              <div className="row"><span>Stop Loss</span><span className="stop">{item.stopLoss}</span></div>
              <div className="divider" />

              <div className="actions-row">
                <button
                  className={`action-button like ${r.myType==='like'?'active':''}`}
                  onClick={()=>handleReaction(item.id,'like')}
                >
                  <FaThumbsUp /> <span className="action-count">{r.likeCount||0}</span>
                </button>
                <button
                  className={`action-button dislike ${r.myType==='dislike'?'active':''}`}
                  onClick={()=>handleReaction(item.id,'dislike')}
                >
                  <FaThumbsDown /> <span className="action-count">{r.dislikeCount||0}</span>
                </button>
                <button
                  className="action-button"
                  onClick={()=>goComments(item.id)}
                >
                  <FaComment /> <span className="action-count">{cCount}</span>
                </button>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
