import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../lib/supabaseClient';
import { SignalsContext } from '../context/SignalsContext';
import './HomePage.css';

export default function HomePage() {
  const navigate = useNavigate();
  const { signals } = useContext(SignalsContext);

  const [user, setUser] = useState(null);
  const [profilesMap, setProfilesMap] = useState({});
  const [reactionsMap, setReactionsMap] = useState({});
  const [commentsCountMap, setCommentsCountMap] = useState({});
  const [loading, setLoading] = useState(true);
  const [searchText, setSearchText] = useState('');

  // 1) Aktif kullanıcıyı al
  useEffect(() => {
    (async () => {
      const { data: { session } } = await supabase.auth.getSession();
      if (session?.user) setUser(session.user);
    })();
  }, []);

  // 2) Profiller, reaksiyonlar, yorum sayıları
  useEffect(() => {
    if (!user || signals.length === 0) return;

    (async () => {
      setLoading(true);

      // Profiller
      const { data: profs } = await supabase
        .from('profiles')
        .select('user_id, full_name');
      const pMap = {};
      profs.forEach(p => (pMap[p.user_id] = p.full_name || 'Anonim'));
      setProfilesMap(pMap);

      // Reaksiyonlar
      const { data: reacts } = await supabase
        .from('reactions')
        .select('signal_id, user_id, type');
      const rMap = {};
      signals.forEach(s => {
        rMap[s.id] = { likeCount: 0, dislikeCount: 0, myType: null };
      });
      reacts.forEach(r => {
        const slot = rMap[r.signal_id];
        if (!slot) return;
        if (r.type === 'like') slot.likeCount++;
        else slot.dislikeCount++;
        if (r.user_id === user.id) slot.myType = r.type;
      });
      setReactionsMap(rMap);

      // Yorum sayıları
      const { data: comms } = await supabase
        .from('comments')
        .select('signal_id');
      const cMap = {};
      signals.forEach(s => (cMap[s.id] = 0));
      comms.forEach(c => {
        if (cMap[c.signal_id] !== undefined) cMap[c.signal_id]++;
      });
      setCommentsCountMap(cMap);

      setLoading(false);
    })();
  }, [signals, user]);

  // 3) Beğeni / beğenmeme handler (yalnızca investor)
  const handleReaction = async (signalId, type) => {
    if (!user) return;
    // Eğer zaten aynı tip varsa sil, farklı tip varsa güncelle, yoksa ekle
    const prev = reactionsMap[signalId]?.myType;
    if (prev === type) {
      await supabase
        .from('reactions')
        .delete()
        .match({ signal_id: signalId, user_id: user.id });
    } else if (prev) {
      await supabase
        .from('reactions')
        .update({ type })
        .match({ signal_id: signalId, user_id: user.id });
    } else {
      await supabase
        .from('reactions')
        .insert({ signal_id: signalId, user_id: user.id, type });
    }
    // yenilemek için basitçe state'i tetikleyelim
    setReactionsMap(prev => ({ ...prev }));
  };

  // 4) Yorum ekranı
  const goComments = id => {
    navigate(`/comments/${id}`);
  };

  // 5) @kullanıcı araması
  const onSearch = async e => {
    e.preventDefault();
    if (!searchText.startsWith('@')) return;
    const name = searchText.slice(1).trim();
    const { data: prof } = await supabase
      .from('profiles')
      .select('user_id')
      .eq('full_name', name)
      .eq('user_type', 'trader')
      .maybeSingle();
    if (prof?.user_id) {
      navigate(`/profile/${prof.user_id}`);
    } else {
      alert(`"${name}" adlı trader bulunamadı.`);
    }
  };

  const filtered = searchText.startsWith('@')
    ? signals
    : signals.filter(s =>
        s.symbol.toLowerCase().includes(searchText.toLowerCase())
      );

  if (loading) return <div className="loader">Yükleniyor…</div>;

  return (
    <div className="home-page">
      <header className="home-header">
        <form onSubmit={onSearch} className="search-form">
          <input
            type="text"
            placeholder="@traderKullanıcıAdi veya sinyal ara…"
            value={searchText}
            onChange={e => setSearchText(e.target.value)}
          />
          <button type="submit">🔍</button>
        </form>
      </header>

      <ul className="signals-list">
        {filtered.map(sig => {
          const username = profilesMap[sig.userId] || 'Anonim';
          const react = reactionsMap[sig.id] || {
            likeCount: 0,
            dislikeCount: 0,
            myType: null
          };
          const cCount = commentsCountMap[sig.id] || 0;
          const d = new Date(sig.timestamp);
          return (
            <li key={sig.id} className="signal-card">
              <div className="user-row">
                <span className="user-icon">👤</span>
                <strong className="user-name">{username}</strong>
              </div>
              <div className="header-row">
                <h3>{sig.symbol}</h3>
                <small>
                  {d.toLocaleDateString()} {d.toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'})}
                </small>
              </div>
              <div className="meta-row">
                <span className={`badge ${sig.direction==='LONG'?'long':'short'}`}>
                  {sig.direction}
                </span>
                <span className="badge timeframe">
                  {sig.timeFrame.toUpperCase()}
                </span>
              </div>
              <div className="divider" />
              <div className="row">
                <span>Entry Price</span>
                <span>{sig.entryPrice}</span>
              </div>
              <div className="row">
                <span>Leverage</span>
                <span>{sig.recommendedLeverage}x</span>
              </div>
              <div className="divider" />
              {sig.targets.map((t,i) => (
                <div key={i} className="row">
                  <span>Target {i+1}</span>
                  <span className="target">{t}</span>
                </div>
              ))}
              <div className="row">
                <span>Stop Loss</span>
                <span className="stop">{sig.stopLoss}</span>
              </div>
              <div className="divider" />
              <div className="actions-row">
                {user?.user_metadata?.user_type === 'investor' ? (
                  <>
                    <button
                      className={`action-button ${react.myType==='like'?'active':''}`}
                      onClick={() => handleReaction(sig.id,'like')}
                    >
                      👍 {react.likeCount}
                    </button>
                    <button
                      className={`action-button ${react.myType==='dislike'?'active':''}`}
                      onClick={() => handleReaction(sig.id,'dislike')}
                    >
                      👎 {react.dislikeCount}
                    </button>
                    <button
                      className="action-button"
                      onClick={() => goComments(sig.id)}
                    >
                      💬 {cCount}
                    </button>
                  </>
                ) : null}
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
