import React, { useState, useEffect } from 'react';
import { supabase } from '../lib/supabaseClient';
import { avatarList } from '../utils/avatars';
import './Header.css';

export default function Header() {
  const [avatarSrc, setAvatarSrc] = useState(null);

  useEffect(() => {
    // Mevcut oturumun profilini Ã§ek
    supabase.auth.getSession().then(async ({ data: { session } }) => {
      const user = session?.user;
      if (user) {
        const { data: prof, error } = await supabase
          .from('profiles')
          .select('avatar_url')
          .eq('user_id', user.id)
          .maybeSingle();
        if (!error && prof?.avatar_url) {
          // avatar_url id'sine gÃ¶re resmi bul
          const item = avatarList.find(a => a.id === prof.avatar_url);
          if (item) setAvatarSrc(item.image);
        }
      }
    });
  }, []);

  return (
    <header className="header">
      <div className="header-left">
        KriptoMentor
      </div>
      <div className="header-center">
        <input
          type="text"
          className="search-input"
          placeholder="Arama..."
        />
      </div>
      <div className="header-right">
        <button className="notif-btn" title="Bildirimler">ðŸ””</button>
        {avatarSrc ? (
          <img
            src={avatarSrc}
            alt="KullanÄ±cÄ± AvatarÄ±"
            className="header-avatar-img"
          />
        ) : (
          <div className="header-avatar-placeholder">ðŸ‘¤</div>
        )}
      </div>
    </header>
  );
}
