// src/App.js

import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { supabase } from './lib/supabaseClient';

import AuthPage from './pages/AuthPage';
import HomePage from './pages/HomePage';
import MarketPage from './pages/MarketPage';
import NewsPage from './pages/NewsPage';
import ProfilePage from './pages/ProfilePage';

function App() {
  // Başlangıçta mevcut kullanıcıyı al
  const [user, setUser] = useState(supabase.auth.user());

  // Auth durum değişikliklerini dinle
  useEffect(() => {
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
    });
    return () => {
      listener.unsubscribe();
    };
  }, []);

  return (
    <Routes>
      {/* Kayıt/Giriş sayfası */}
      <Route path="/auth" element={<AuthPage />} />

      {/* Korunmuş rotalar */}
      <Route
        path="/"
        element={user ? <HomePage /> : <Navigate to="/auth" replace />}
      />
      <Route
        path="/market"
        element={user ? <MarketPage /> : <Navigate to="/auth" replace />}
      />
      <Route
        path="/news"
        element={user ? <NewsPage /> : <Navigate to="/auth" replace />}
      />
      <Route
        path="/profile"
        element={user ? <ProfilePage /> : <Navigate to="/auth" replace />}
      />

      {/* Tanımsız rotalar */}
      <Route
        path="*"
        element={<Navigate to={user ? "/" : "/auth"} replace />}
      />
    </Routes>
  );
}

export default App;
