import React, { useEffect, useState, useContext } from 'react';
import { useParams, useNavigate }     from 'react-router-dom';
import { supabase }                   from '../lib/supabaseClient';
import { avatarList }                 from '../utils/avatars';
import { SignalsContext }             from '../context/SignalsContext';
import './PublicProfilePage.css';

export default function PublicProfilePage() {
  const { userId } = useParams();
  const navigate   = useNavigate();
  const { signals: allSignals } = useContext(SignalsContext);

  const [profile, setProfile] = useState(null);
  const [signals, setSignals] = useState([]);
  const [loading, setLoading] = useState(true);

  // ‚Äî Takip i√ßin state‚Äôler
  const [currentUser, setCurrentUser]     = useState(null);
  const [myProfileType, setMyProfileType] = useState(null);
  const [followersCount, setFollowersCount] = useState(0);
  const [isFollowing, setIsFollowing]     = useState(false);

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      // 1) Profil verisini √ßek
      const { data: prof, error: profErr } = await supabase
        .from('profiles')
        .select('full_name, bio, avatar_url, created_at')
        .eq('user_id', userId)
        .maybeSingle();
      if (profErr) {
        console.error(profErr);
        navigate('/', { replace: true });
        return;
      }
      setProfile(prof || { full_name: '', bio: '', avatar_url: '', created_at: new Date() });

      // 2) Sinyalleri SignalsContext'ten filtrele
      const userSignals = allSignals.filter(s => s.userId === userId);
      setSignals(userSignals);

      // 3) Oturumlu kullanƒ±cƒ± ve tipi
      const { data: { session } } = await supabase.auth.getSession();
      const usr = session?.user;
      if (usr) {
        setCurrentUser(usr);
        const { data: meProf } = await supabase
          .from('profiles')
          .select('user_type')
          .eq('user_id', usr.id)
          .maybeSingle();
        setMyProfileType(meProf?.user_type);
      }

      // 4) Takip√ßi sayƒ±sƒ±nƒ± √ßek
      const { count } = await supabase
        .from('follows')
        .select('*', { count: 'exact', head: true })
        .eq('trader_id', userId);
      setFollowersCount(count);

      // 5) Yatƒ±rƒ±mcƒ±ysa, zaten takip ediyor mu?
      if (myProfileType === 'investor' && currentUser) {
        const { data: existing } = await supabase
          .from('follows')
          .select('*')
          .eq('trader_id', userId)
          .eq('investor_id', currentUser.id)
          .maybeSingle();
        setIsFollowing(!!existing);
      }

      setLoading(false);
    };
    load();
  }, [userId, allSignals, navigate, myProfileType, currentUser]);

  // follow / unfollow handler
  const toggleFollow = async () => {
    if (!currentUser) return;
    if (isFollowing) {
      await supabase
        .from('follows')
        .delete()
        .match({ trader_id: userId, investor_id: currentUser.id });
      setFollowersCount(c => c - 1);
    } else {
      await supabase
        .from('follows')
        .insert({ trader_id: userId, investor_id: currentUser.id });
      setFollowersCount(c => c + 1);
    }
    setIsFollowing(f => !f);
  };

  if (loading) {
    return <div className="public-profile loader">Y√ºkleniyor‚Ä¶</div>;
  }

  // avatar kaynaƒüƒ±
  const avatarItem = avatarList.find(a => a.id === profile.avatar_url);
  const avatarSrc  = avatarItem?.image;

  return (
    <div className="public-profile">
      <button className="back-btn" onClick={() => navigate(-1)}>
        ‚Üê Geri
      </button>
      <div className="profile-header">
        {avatarSrc
          ? <img src={avatarSrc} alt="Avatar" className="avatar-img"/>
          : <div className="avatar-placeholder">üë§</div>
        }
        <h2 className="name">{profile.full_name || 'KriptoMentor Kullanƒ±cƒ±sƒ±'}</h2>
        {profile.bio && <p className="bio">{profile.bio}</p>}
        <p className="joined">
          Katƒ±lƒ±m: {new Date(profile.created_at).toLocaleDateString()}
        </p>

        {/* ‚Äî Takip√ßi sayƒ±sƒ± */}
        <p className="followers-count">Takip√ßi: {followersCount}</p>

        {/* ‚Äî Yatƒ±rƒ±mcƒ± bakƒ±≈üƒ±ndaysa buton */}
        {myProfileType === 'investor' && (
          <button
            className={`follow-btn ${isFollowing ? 'unfollow' : 'follow'}`}
            onClick={toggleFollow}
          >
            {isFollowing ? 'Takibi Bƒ±rak' : 'Takip Et'}
          </button>
        )}
      </div>

      <h3 className="signals-title">Payla≈ütƒ±ƒüƒ± Sinyaller</h3>
      {signals.length === 0 ? (
        <p className="empty">Hen√ºz sinyal payla≈ümamƒ±≈ü.</p>
      ) : (
        <ul className="signals-list">
          {signals.map(item => {
            const d       = new Date(item.timestamp);
            const dateStr = d.toLocaleDateString();
            const timeStr = d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
            return (
              <li key={item.id} className="signal-card">
                <div className="card-header">
                  <span className="symbol">{item.symbol}</span>
                  <span className="timestamp">{dateStr} {timeStr}</span>
                </div>
                <div className="card-meta">
                  <span className={`badge ${item.direction === 'LONG' ? 'long' : 'short'}`}>
                    {item.direction}
                  </span>
                  <span className="badge timeframe">
                    {item.timeFrame?.toUpperCase()}
                  </span>
                </div>
                <div className="card-body">
                  <div className="row">
                    <span className="label">Entry Price</span>
                    <span className="value">{item.entryPrice}</span>
                  </div>
                  <div className="row">
                    <span className="label">Leverage</span>
                    <span className="value">{item.recommendedLeverage}x</span>
                  </div>
                  {item.targets.map((t,i) => (
                    <div className="row" key={i}>
                      <span className="label">Target {i+1}</span>
                      <span className="value target">{t}</span>
                    </div>
                  ))}
                  <div className="row">
                    <span className="label">Stop Loss</span>
                    <span className="value stop">{item.stopLoss}</span>
                  </div>
                </div>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
