import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    // Şimdilik statik kullanıcı profili verisi kullanılıyor
    axios.get('/api/profile')
      .then(response => {
        setProfile(response.data);
      })
      .catch(error => {
        console.error('Profil alınamadı:', error);
      });
  }, []);

  return (
    <div className="profile-page">
      <h2>Kullanıcı Profil</h2>
      {profile ? (
        <div className="profile-details">
          <p><strong>Kullanıcı Adı:</strong> {profile.username}</p>
          <p><strong>Trader Değerlendirmesi:</strong> {profile.rating}</p>
          <p><strong>Katılım Tarihi:</strong> {profile.joinDate}</p>
        </div>
      ) : (
        <p>Profil yükleniyor...</p>
      )}
    </div>
  );
};

export default ProfilePage;
