import React from 'react';
import './NewsPage.css';
import NewsItem from '../components/NewsItem';

export default function NewsPage() {
  const dummyNews = [
    { id: 1, title: 'BTC Yükseliyor', summary: 'Bitcoin fiyatları son günlerde dalgalanıyor…', url: '#' },
    { id: 2, title: 'ETH Hard Fork', summary: 'Ethereum ağı yeni güncellemeye hazırlanıyor…', url: '#' },
  ];

  return (
    <div className="news-page">
      <h2>Güncel Kripto Haberleri</h2>
      <div className="news-layout">
        <section className="news-cards">
          {dummyNews.map(item => (
            <NewsItem key={item.id} news={item} />
          ))}
        </section>
        <aside className="news-right-panel">
          <h3>Popüler Etiketler</h3>
          <ul className="tags">
            <li>NFT</li>
            <li>DeFi</li>
            <li>Regülasyonlar</li>
          </ul>
          <div className="news-search">
            <input type="text" placeholder="Haber ara..." />
          </div>
        </aside>
      </div>
    </div>
  );
}
