import React, { useState } from 'react';
import { signUp, signIn } from '../services/auth';
import './AuthPage.css';

export default function AuthPage() {
  const [mode, setMode] = useState('login'); // 'login' veya 'signup'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    if (mode === 'signup') {
      const { error } = await signUp(email, password);
      setMessage(error ? error.message : 'Kaydınız oluşturuldu! Lütfen e‑postanızı onaylayın.');
    } else {
      const { error } = await signIn(email, password);
      setMessage(error ? error.message : 'Giriş başarılı! Yönlendiriliyorsunuz...');
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2>{mode === 'signup' ? 'Kayıt Ol' : 'Giriş Yap'}</h2>
        <form onSubmit={handleSubmit}>
          <label>
            E‑posta
            <input
              type="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
          </label>
          <label>
            Parola
            <input
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
          </label>
          {message && <p className="auth-message">{message}</p>}
          <button type="submit" className="auth-button">
            {mode === 'signup' ? 'Kayıt Ol' : 'Giriş Yap'}
          </button>
        </form>
        <p className="switch-text">
          {mode === 'signup'
            ? 'Zaten hesabınız var mı?'
            : 'Hesabınız yok mu?'}
          <button
            className="switch-button"
            onClick={() => {
              setMode(mode === 'signup' ? 'login' : 'signup');
              setMessage('');
            }}
          >
            {mode === 'signup' ? 'Giriş Yap' : 'Kayıt Ol'}
          </button>
        </p>
      </div>
    </div>
  );
}
